package com.acharya.classs;

import java.util.*;

public class ArraySum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int arr1[] = { 12, 21, 11, 43 };
		int sum=0;

		for (int i = 0; i < arr1.length; i++) {
			sum+=arr1[i];
			
		}
		System.out.println(sum);

	}

}
